import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { deleteAllFromCart, deleteFromCart } from "../redux/actions/productActions";

const totalPrice = 0



function CartData() {
  const state = useSelector((state) => state.productReducer.cart);
  const price = useSelector(state=>state.productReducer.cart)
  // console.log(price)
    const dispatch = useDispatch()
    const navigate = useNavigate()
    useEffect(() => {
      if (!localStorage.getItem('token')) {
        navigate('/login')
      }
    }, [])
   
   console.log(priceData)
    const handleDeleteBtn = (id)=>{
      console.log(id)
      dispatch(deleteFromCart(id))
      toast.success("Deleted from card successfully")
    }
    const showCartData = ()=>{
        return(
            <div className="row">
        {state.map((items) => (
          
          <>
         
            <div className="col-md-6 mt-4 ">
              <img src={items.image} alt="" height={"200px"} width={'300px'} />
            </div>
            <div className="col-md-6 mt-4">
              <button className="btn btn-outline-dark float-end" onClick={()=>handleDeleteBtn(items.id)}>
                Remove from cart
              </button>
              <h2 className=" mt-4">{items.title}</h2>
              <p>
                <span className="fw-bold">Price : $</span> {items.price}
              </p>
            </div>
            <hr />
          </>
        ))}
      </div>
        )
    }

    const showMessage = ()=>{
        return <span >Cart is empty</span>
    }
    const handleCheckOutBtn = ()=>{
      dispatch(deleteAllFromCart())
      toast.success("purchase complete")
      navigate('/')
    }
  return (
    <div className="container">
      {
       state.length? showCartData() : showMessage()
      }
      <div className="price_div">
        <h4>Total Price :{totalPrice}</h4>
      </div>
      <button className="btn btn-outline-dark mt-5 float-end" onClick={()=>handleCheckOutBtn()}>CheckOut</button>
    </div>
  );
}

export default CartData;
