import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { handleCategoryData, handleProductData } from '../ActionController'

function Tabs() {
const [data , setData] = useState([])
const [tabs , setTabs] = useState([])
const [filterData , setFilterData] = useState([])
const showData = useSelector(state=>state.productReducer.search)
console.log(showData)

    const showProductData = async()=>{
        try{
            const res = await handleProductData()
            setData(res)
        }catch(e){
            console.log(e.message)
        }
    }

    const showCategoryData = async()=>{
        try{
            const res = await handleCategoryData()
            setFilterData(res)
            setTabs(res)
        }catch(e){
            console.log(e.message)
        }
    }

    useEffect(()=>{
        showProductData()
        showCategoryData()
    } , [])

    const handleCatgBtn = (catg)=>{
        const fetch = data.filter((x)=>{
            if(x.category === catg){
               return x
            }
        })
        return setFilterData(fetch)
    }

    const showTabs = ()=>{
        return(
            <div className=' tabs_div'>
            <button className='btn btn-outline-dark' onClick={()=>setFilterData(data)}>All</button>
                {
                    tabs.map((items)=>(
                        <button className='btn btn-outline-dark' onClick={()=>handleCatgBtn(items)}>{items}</button>
                    ))
                }
            </div>
        )
    }
  return (
    <div className='container'>
    {
        showTabs()
    }
        <div className="row">
            {
                filterData.map((items)=>(
                    <div className="col-md-3">
                        <div className="card" key={items.id}>
                            <img src={items.thumbnail} alt="" height={'200px'} />
                            <div className="card-body">
                                <p>{items.title}</p>
                            </div>
                        </div>
                    </div>
                ))
            }
        </div>
    </div>
  )
}

export default Tabs
