import axios from 'axios'
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

function Login() {
    const [email , setEmail] = useState('')
    const [password , setPassword] = useState('')
    const navigate = useNavigate()

    const handleLogin = ()=>{
        axios.post('https://reqres.in/api/login' , {
            email : email,
            password : password
        }).then((result)=>{
            console.log(result.data)
            localStorage.setItem('token' , result.data.token)
            alert('successfully login')
            navigate('/')
        }).catch(err=>{
            console.log(err)
            alert("please login first")
        })
    }
  return (
    <div className="  login_form">
    <h1 className='login_h1'>Login page</h1>
    <input type="text"  placeholder='Enter your Email' value={email} onChange={()=>setEmail(e.target.value)} /><br />
    <input type="text"  placeholder='Enter your password' value={password} onChange={()=>setPassword(e.target.value)} /> 
    <button className="login_btn" onClick={handleLogin}>Login</button>
    </div>
  )
}

export default Login
