const addAllProducts = (products)=>{
    return {
        type : 'ALL_PRODUCTS',
        payload  : products
    }
}
const deleteProduct = (id)=>{
    return {
        type : 'DELETE_PRODUCT',
        id
    }
}
const addTOCart = (data)=>{
    return {
        type : 'ADD_CART',
        payload : data
    }
}
const addSearch = (data)=>{
    return {
        type : 'SEARCH',
        payload : data
    }
}

export {addAllProducts,addTOCart, deleteProduct ,  addSearch}
