import React, { useEffect, useState } from 'react'
import { handleCategoryData, handleProductData } from '../ActionController'

function Tabs() {
const [data , setData] = useState([])
const [tabs , setTabs] = useState([])
    const showProductData = async()=>{
        try{
            const res = await handleProductData()
            console.log(res)
        }catch(e){
            console.log(e.message)
        }
    }

    const showCategoryData = async()=>{
        try{
            const res = await handleCategoryData()
            setTabs(res)
        }catch(e){
            console.log(e.message)
        }
    }

    useEffect(()=>{
        showProductData()
        showCategoryData()
    } , [])

    const showTabs = ()=>{
        return(
            <div className="row">
                {
                    tabs.map((items)=>(
                        <button className='btn btn-outline-dark'>{items}</button>
                    ))
                }
            </div>
        )
    }
  return (
    <div className='container'>
    {
        showTabs()
    }
        <div className="row">

        </div>
    </div>
  )
}

export default Tabs
