import React from "react";
import { useDispatch, useSelector } from "react-redux";




function CartData() {
  const state = useSelector((state) => state.productReducer.products);
    const dispatch = useDispatch()

    const showCartData = ()=>{
        return(
            <div className="row mt-5">
        {state.map((items) => (
          <>
            <div className="col-md-6">
              <img src={items.thumbnail} alt="" height={"300px"} />
            </div>
            <div className="col-md-6">
              <button className="btn btn-outline-dark float-end">
                delete
              </button>
              <p>{items.title}</p>
              <p>
                <span className="fw-bold">Price : $</span> {items.price}
              </p>
            </div>
          </>
        ))}
      </div>
        )
    }

    const showMessage = ()=>{
        return <span>Cart is empty</span>
    }
  return (
    <div className="container">
      {
       state.length? showCartData() : showMessage()
      }
    </div>
  );
}

export default CartData;
