const initialState = {
    products : [],
    search  : '',
    cart : []
}

const productReducer = (state = initialState , action)=>{
    switch(action.type){
        case 'SEARCH':
            return {
                ...state,
                search : action.payload
            }
        case 'ALL_PRODUCTS':{
            return {
                ...state,
                products : action.payload
            }
        }
        case 'DELETE_PRODUCT':
            const filterData = state.products.filter(x=>x.id !== action.id && x)
            return {
            ...state,
            products : filterData  
        }
        case 'ADD_CART':
            return {
                ...state,
                cart : [...state.cart , action.payload]
            }

            default :
            return state
    }
}

export default productReducer
